# [ doc = "The heartbeat message shows that a system is present and responding. The type of the MAV and Autopilot hardware allow the receiving system to treat further messages from this system appropriate (e.g. by laying out the user interface based on the autopilot)." ] pub struct HEARTBEAT { # [ doc = "Type of the MAV (quadrotor, helicopter, etc., up to 15 types, defined in MAV_TYPE ENUM)" ] typ : u8 , # [ doc = "Type of the Autopilot: 0: Generic, 1: PIXHAWK, 2: SLUGS, 3: Ardupilot (up to 15 types), defined in MAV_AUTOPILOT_TYPE ENUM" ] autopilot : u8 , # [ doc = "MAVLink version" ] mavlink_version : u8 , } # [ doc = "The boot message indicates that a system is starting. The onboard software version allows to keep track of onboard soft/firmware revisions." ] pub struct BOOT { # [ doc = "The onboard software version" ] version : u32 , } # [ doc = "The system time is the time of the master clock, typically the computer clock of the main onboard computer." ] pub struct SYSTEM_TIME { # [ doc = "Timestamp of the master clock in microseconds since UNIX epoch." ] time_usec : u64 , } # [ doc = "A ping message either requesting or responding to a ping. This allows to measure the system latencies, including serial port, radio modem and UDP connections." ] pub struct PING { # [ doc = "PING sequence" ] seq : u32 , # [ doc = "0: request ping from all receiving systems, if greater than 0: message is a ping response and number is the system id of the requesting system" ] target_system : u8 , # [ doc = "0: request ping from all receiving components, if greater than 0: message is a ping response and number is the system id of the requesting system" ] target_component : u8 , # [ doc = "Unix timestamp in microseconds" ] time : u64 , } # [ doc = "UTC date and time from GPS module" ] pub struct SYSTEM_TIME_UTC { # [ doc = "GPS UTC date ddmmyy" ] utc_date : u32 , # [ doc = "GPS UTC time hhmmss" ] utc_time : u32 , } # [ doc = "Request to control this MAV" ] pub struct CHANGE_OPERATOR_CONTROL { # [ doc = "System the GCS requests control for" ] target_system : u8 , # [ doc = "0: request control of this MAV, 1: Release control of this MAV" ] control_request : u8 , # [ doc = "0: key as plaintext, 1-255: future, different hashing/encryption variants. The GCS should in general use the safest mode possible initially and then gradually move down the encryption level if it gets a NACK message indicating an encryption mismatch." ] version : u8 , # [ doc = "Password / Key, depending on version plaintext or encrypted. 25 or less characters, NULL terminated. The characters may involve A-Z, a-z, 0-9, and \"!?,.-\"" ] passkey : u8 , } # [ doc = "Accept / deny control of this MAV" ] pub struct CHANGE_OPERATOR_CONTROL_ACK { # [ doc = "ID of the GCS this message " ] gcs_system_id : u8 , # [ doc = "0: request control of this MAV, 1: Release control of this MAV" ] control_request : u8 , # [ doc = "0: ACK, 1: NACK: Wrong passkey, 2: NACK: Unsupported passkey encryption method, 3: NACK: Already under control" ] ack : u8 , } # [ doc = "Emit an encrypted signature / key identifying this system. PLEASE NOTE: This protocol has been kept simple, so transmitting the key requires an encrypted channel for true safety." ] pub struct AUTH_KEY { # [ doc = "key" ] key : u8 , } # [ doc = "This message acknowledges an action. IMPORTANT: The acknowledgement can be also negative, e.g. the MAV rejects a reset message because it is in-flight. The action ids are defined in ENUM MAV_ACTION in mavlink/include/mavlink_types.h" ] pub struct ACTION_ACK { # [ doc = "The action id" ] action : u8 , # [ doc = "0: Action DENIED, 1: Action executed" ] result : u8 , } # [ doc = "An action message allows to execute a certain onboard action. These include liftoff, land, storing parameters too EEPROM, shutddown, etc. The action ids are defined in ENUM MAV_ACTION in mavlink/include/mavlink_types.h" ] pub struct ACTION { # [ doc = "The system executing the action" ] target : u8 , # [ doc = "The component executing the action" ] target_component : u8 , # [ doc = "The action id" ] action : u8 , } # [ doc = "Set the system mode, as defined by enum MAV_MODE in mavlink/include/mavlink_types.h. There is no target component id as the mode is by definition for the overall aircraft, not only for one component." ] pub struct SET_MODE { # [ doc = "The system setting the mode" ] target : u8 , # [ doc = "The new mode" ] mode : u8 , } # [ doc = "Set the system navigation mode, as defined by enum MAV_NAV_MODE in mavlink/include/mavlink_types.h. The navigation mode applies to the whole aircraft and thus all components." ] pub struct SET_NAV_MODE { # [ doc = "The system setting the mode" ] target : u8 , # [ doc = "The new navigation mode" ] nav_mode : u8 , } # [ doc = "Request to read the onboard parameter with the param_id string id. Onboard parameters are stored as key[const char*] -> value[float]. This allows to send a parameter to any other component (such as the GCS) without the need of previous knowledge of possible parameter names. Thus the same GCS can store different parameters for different autopilots. See also http://qgroundcontrol.org/parameter_interface for a full documentation of QGroundControl and IMU code." ] pub struct PARAM_REQUEST_READ { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "Onboard parameter id" ] param_id : u8 , # [ doc = "Parameter index. Send -1 to use the param ID field as identifier" ] param_index : i16 , } # [ doc = "Request all parameters of this component. After his request, all parameters are emitted." ] pub struct PARAM_REQUEST_LIST { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , } # [ doc = "Emit the value of a onboard parameter. The inclusion of param_count and param_index in the message allows the recipient to keep track of received parameters and allows him to re-request missing parameters after a loss or timeout." ] pub struct PARAM_VALUE { # [ doc = "Onboard parameter id" ] param_id : u8 , # [ doc = "Onboard parameter value" ] param_value : f32 , # [ doc = "Total number of onboard parameters" ] param_count : u16 , # [ doc = "Index of this onboard parameter" ] param_index : u16 , } # [ doc = "Set a parameter value TEMPORARILY to RAM. It will be reset to default on system reboot. Send the ACTION MAV_ACTION_STORAGE_WRITE to PERMANENTLY write the RAM contents to EEPROM. IMPORTANT: The receiving component should acknowledge the new parameter value by sending a param_value message to all communication partners. This will also ensure that multiple GCS all have an up-to-date list of all parameters. If the sending GCS did not receive a PARAM_VALUE message within its timeout time, it should re-send the PARAM_SET message." ] pub struct PARAM_SET { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "Onboard parameter id" ] param_id : u8 , # [ doc = "Onboard parameter value" ] param_value : f32 , } # [ doc = "The global position, as returned by the Global Positioning System (GPS). This is\nNOT the global position estimate of the sytem, but rather a RAW sensor value. See message GLOBAL_POSITION for the global position estimate. Coordinate frame is right-handed, Z-axis up (GPS frame)" ] pub struct GPS_RAW_INT { # [ doc = "Timestamp (microseconds since UNIX epoch or microseconds since system boot)" ] usec : u64 , # [ doc = "0-1: no fix, 2: 2D fix, 3: 3D fix. Some applications will not use the value of this field unless it is at least two, so always correctly fill in the fix." ] fix_type : u8 , # [ doc = "Latitude in 1E7 degrees" ] lat : i32 , # [ doc = "Longitude in 1E7 degrees" ] lon : i32 , # [ doc = "Altitude in 1E3 meters (millimeters)" ] alt : i32 , # [ doc = "GPS HDOP" ] eph : f32 , # [ doc = "GPS VDOP" ] epv : f32 , # [ doc = "GPS ground speed (m/s)" ] v : f32 , # [ doc = "Compass heading in degrees, 0..360 degrees" ] hdg : f32 , } # [ doc = "The RAW IMU readings for the usual 9DOF sensor setup. This message should contain the scaled values to the described units" ] pub struct SCALED_IMU { # [ doc = "Timestamp (microseconds since UNIX epoch or microseconds since system boot)" ] usec : u64 , # [ doc = "X acceleration (mg)" ] xacc : i16 , # [ doc = "Y acceleration (mg)" ] yacc : i16 , # [ doc = "Z acceleration (mg)" ] zacc : i16 , # [ doc = "Angular speed around X axis (millirad /sec)" ] xgyro : i16 , # [ doc = "Angular speed around Y axis (millirad /sec)" ] ygyro : i16 , # [ doc = "Angular speed around Z axis (millirad /sec)" ] zgyro : i16 , # [ doc = "X Magnetic field (milli tesla)" ] xmag : i16 , # [ doc = "Y Magnetic field (milli tesla)" ] ymag : i16 , # [ doc = "Z Magnetic field (milli tesla)" ] zmag : i16 , } # [ doc = "The positioning status, as reported by GPS. This message is intended to display status information about each satellite visible to the receiver. See message GLOBAL_POSITION for the global position estimate. This message can contain information for up to 20 satellites." ] pub struct GPS_STATUS { # [ doc = "Number of satellites visible" ] satellites_visible : u8 , # [ doc = "Global satellite ID" ] satellite_prn : u8 , # [ doc = "0: Satellite not used, 1: used for localization" ] satellite_used : u8 , # [ doc = "Elevation (0: right on top of receiver, 90: on the horizon) of satellite" ] satellite_elevation : u8 , # [ doc = "Direction of satellite, 0: 0 deg, 255: 360 deg." ] satellite_azimuth : u8 , # [ doc = "Signal to noise ratio of satellite" ] satellite_snr : u8 , } # [ doc = "The RAW IMU readings for the usual 9DOF sensor setup. This message should always contain the true raw values without any scaling to allow data capture and system debugging." ] pub struct RAW_IMU { # [ doc = "Timestamp (microseconds since UNIX epoch or microseconds since system boot)" ] usec : u64 , # [ doc = "X acceleration (raw)" ] xacc : i16 , # [ doc = "Y acceleration (raw)" ] yacc : i16 , # [ doc = "Z acceleration (raw)" ] zacc : i16 , # [ doc = "Angular speed around X axis (raw)" ] xgyro : i16 , # [ doc = "Angular speed around Y axis (raw)" ] ygyro : i16 , # [ doc = "Angular speed around Z axis (raw)" ] zgyro : i16 , # [ doc = "X Magnetic field (raw)" ] xmag : i16 , # [ doc = "Y Magnetic field (raw)" ] ymag : i16 , # [ doc = "Z Magnetic field (raw)" ] zmag : i16 , } # [ doc = "The RAW pressure readings for the typical setup of one absolute pressure and one differential pressure sensor. The sensor values should be the raw, UNSCALED ADC values." ] pub struct RAW_PRESSURE { # [ doc = "Timestamp (microseconds since UNIX epoch or microseconds since system boot)" ] usec : u64 , # [ doc = "Absolute pressure (raw)" ] press_abs : i16 , # [ doc = "Differential pressure 1 (raw)" ] press_diff1 : i16 , # [ doc = "Differential pressure 2 (raw)" ] press_diff2 : i16 , # [ doc = "Raw Temperature measurement (raw)" ] temperature : i16 , } # [ doc = "The pressure readings for the typical setup of one absolute and differential pressure sensor. The units are as specified in each field." ] pub struct SCALED_PRESSURE { # [ doc = "Timestamp (microseconds since UNIX epoch or microseconds since system boot)" ] usec : u64 , # [ doc = "Absolute pressure (hectopascal)" ] press_abs : f32 , # [ doc = "Differential pressure 1 (hectopascal)" ] press_diff : f32 , # [ doc = "Temperature measurement (0.01 degrees celsius)" ] temperature : i16 , } # [ doc = "The attitude in the aeronautical frame (right-handed, Z-down, X-front, Y-right)." ] pub struct ATTITUDE { # [ doc = "Timestamp (microseconds since UNIX epoch or microseconds since system boot)" ] usec : u64 , # [ doc = "Roll angle (rad)" ] roll : f32 , # [ doc = "Pitch angle (rad)" ] pitch : f32 , # [ doc = "Yaw angle (rad)" ] yaw : f32 , # [ doc = "Roll angular speed (rad/s)" ] rollspeed : f32 , # [ doc = "Pitch angular speed (rad/s)" ] pitchspeed : f32 , # [ doc = "Yaw angular speed (rad/s)" ] yawspeed : f32 , } # [ doc = "The filtered local position (e.g. fused computer vision and accelerometers). Coordinate frame is right-handed, Z-axis down (aeronautical frame)" ] pub struct LOCAL_POSITION { # [ doc = "Timestamp (microseconds since UNIX epoch or microseconds since system boot)" ] usec : u64 , # [ doc = "X Position" ] x : f32 , # [ doc = "Y Position" ] y : f32 , # [ doc = "Z Position" ] z : f32 , # [ doc = "X Speed" ] vx : f32 , # [ doc = "Y Speed" ] vy : f32 , # [ doc = "Z Speed" ] vz : f32 , } # [ doc = "The filtered global position (e.g. fused GPS and accelerometers). Coordinate frame is right-handed, Z-axis up (GPS frame)" ] pub struct GLOBAL_POSITION { # [ doc = "Timestamp (microseconds since unix epoch)" ] usec : u64 , # [ doc = "Latitude, in degrees" ] lat : f32 , # [ doc = "Longitude, in degrees" ] lon : f32 , # [ doc = "Absolute altitude, in meters" ] alt : f32 , # [ doc = "X Speed (in Latitude direction, positive: going north)" ] vx : f32 , # [ doc = "Y Speed (in Longitude direction, positive: going east)" ] vy : f32 , # [ doc = "Z Speed (in Altitude direction, positive: going up)" ] vz : f32 , } # [ doc = "The global position, as returned by the Global Positioning System (GPS). This is\nNOT the global position estimate of the sytem, but rather a RAW sensor value. See message GLOBAL_POSITION for the global position estimate. Coordinate frame is right-handed, Z-axis up (GPS frame)" ] pub struct GPS_RAW { # [ doc = "Timestamp (microseconds since UNIX epoch or microseconds since system boot)" ] usec : u64 , # [ doc = "0-1: no fix, 2: 2D fix, 3: 3D fix. Some applications will not use the value of this field unless it is at least two, so always correctly fill in the fix." ] fix_type : u8 , # [ doc = "Latitude in degrees" ] lat : f32 , # [ doc = "Longitude in degrees" ] lon : f32 , # [ doc = "Altitude in meters" ] alt : f32 , # [ doc = "GPS HDOP" ] eph : f32 , # [ doc = "GPS VDOP" ] epv : f32 , # [ doc = "GPS ground speed" ] v : f32 , # [ doc = "Compass heading in degrees, 0..360 degrees" ] hdg : f32 , } # [ doc = "The general system state. If the system is following the MAVLink standard, the system state is mainly defined by three orthogonal states/modes: The system mode, which is either LOCKED (motors shut down and locked), MANUAL (system under RC control), GUIDED (system with autonomous position control, position setpoint controlled manually) or AUTO (system guided by path/waypoint planner). The NAV_MODE defined the current flight state: LIFTOFF (often an open-loop maneuver), LANDING, WAYPOINTS or VECTOR. This represents the internal navigation state machine. The system status shows whether the system is currently active or not and if an emergency occured. During the CRITICAL and EMERGENCY states the MAV is still considered to be active, but should start emergency procedures autonomously. After a failure occured it should first move from active to critical to allow manual intervention and then move to emergency after a certain timeout." ] pub struct SYS_STATUS { # [ doc = "System mode, see MAV_MODE ENUM in mavlink/include/mavlink_types.h" ] mode : u8 , # [ doc = "Navigation mode, see MAV_NAV_MODE ENUM" ] nav_mode : u8 , # [ doc = "System status flag, see MAV_STATUS ENUM" ] status : u8 , # [ doc = "Maximum usage in percent of the mainloop time, (0%: 0, 100%: 1000) should be always below 1000" ] load : u16 , # [ doc = "Battery voltage, in millivolts (1 = 1 millivolt)" ] vbat : u16 , # [ doc = "Remaining battery energy: (0%: 0, 100%: 1000)" ] battery_remaining : u16 , # [ doc = "Dropped packets (packets that were corrupted on reception on the MAV)" ] packet_drop : u16 , } # [ doc = "The RAW values of the RC channels received. The standard PPM modulation is as follows: 1000 microseconds: 0%, 2000 microseconds: 100%. Individual receivers/transmitters might violate this specification." ] pub struct RC_CHANNELS_RAW { # [ doc = "RC channel 1 value, in microseconds" ] chan1_raw : u16 , # [ doc = "RC channel 2 value, in microseconds" ] chan2_raw : u16 , # [ doc = "RC channel 3 value, in microseconds" ] chan3_raw : u16 , # [ doc = "RC channel 4 value, in microseconds" ] chan4_raw : u16 , # [ doc = "RC channel 5 value, in microseconds" ] chan5_raw : u16 , # [ doc = "RC channel 6 value, in microseconds" ] chan6_raw : u16 , # [ doc = "RC channel 7 value, in microseconds" ] chan7_raw : u16 , # [ doc = "RC channel 8 value, in microseconds" ] chan8_raw : u16 , # [ doc = "Receive signal strength indicator, 0: 0%, 255: 100%" ] rssi : u8 , } # [ doc = "The scaled values of the RC channels received. (-100%) -10000, (0%) 0, (100%) 10000" ] pub struct RC_CHANNELS_SCALED { # [ doc = "RC channel 1 value scaled, (-100%) -10000, (0%) 0, (100%) 10000" ] chan1_scaled : i16 , # [ doc = "RC channel 2 value scaled, (-100%) -10000, (0%) 0, (100%) 10000" ] chan2_scaled : i16 , # [ doc = "RC channel 3 value scaled, (-100%) -10000, (0%) 0, (100%) 10000" ] chan3_scaled : i16 , # [ doc = "RC channel 4 value scaled, (-100%) -10000, (0%) 0, (100%) 10000" ] chan4_scaled : i16 , # [ doc = "RC channel 5 value scaled, (-100%) -10000, (0%) 0, (100%) 10000" ] chan5_scaled : i16 , # [ doc = "RC channel 6 value scaled, (-100%) -10000, (0%) 0, (100%) 10000" ] chan6_scaled : i16 , # [ doc = "RC channel 7 value scaled, (-100%) -10000, (0%) 0, (100%) 10000" ] chan7_scaled : i16 , # [ doc = "RC channel 8 value scaled, (-100%) -10000, (0%) 0, (100%) 10000" ] chan8_scaled : i16 , # [ doc = "Receive signal strength indicator, 0: 0%, 255: 100%" ] rssi : u8 , } # [ doc = "The RAW values of the servo outputs (for RC input from the remote, use the RC_CHANNELS messages). The standard PPM modulation is as follows: 1000 microseconds: 0%, 2000 microseconds: 100%." ] pub struct SERVO_OUTPUT_RAW { # [ doc = "Servo output 1 value, in microseconds" ] servo1_raw : u16 , # [ doc = "Servo output 2 value, in microseconds" ] servo2_raw : u16 , # [ doc = "Servo output 3 value, in microseconds" ] servo3_raw : u16 , # [ doc = "Servo output 4 value, in microseconds" ] servo4_raw : u16 , # [ doc = "Servo output 5 value, in microseconds" ] servo5_raw : u16 , # [ doc = "Servo output 6 value, in microseconds" ] servo6_raw : u16 , # [ doc = "Servo output 7 value, in microseconds" ] servo7_raw : u16 , # [ doc = "Servo output 8 value, in microseconds" ] servo8_raw : u16 , } # [ doc = "Message encoding a waypoint. This message is emitted to announce\n     the presence of a waypoint and to set a waypoint on the system. The waypoint can be either in x, y, z meters (type: LOCAL) or x:lat, y:lon, z:altitude. Local frame is Z-down, right handed, global frame is Z-up, right handed" ] pub struct WAYPOINT { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "Sequence" ] seq : u16 , # [ doc = "The coordinate system of the waypoint. see MAV_FRAME in mavlink_types.h" ] frame : u8 , # [ doc = "The scheduled action for the waypoint. see MAV_COMMAND in common.xml MAVLink specs" ] command : u8 , # [ doc = "false:0, true:1" ] current : u8 , # [ doc = "autocontinue to next wp" ] autocontinue : u8 , # [ doc = "PARAM1 / For NAV command waypoints: Radius in which the waypoint is accepted as reached, in meters" ] param1 : f32 , # [ doc = "PARAM2 / For NAV command waypoints: Time that the MAV should stay inside the PARAM1 radius before advancing, in milliseconds" ] param2 : f32 , # [ doc = "PARAM3 / For LOITER command waypoints: Orbit to circle around the waypoint, in meters. If positive the orbit direction should be clockwise, if negative the orbit direction should be counter-clockwise." ] param3 : f32 , # [ doc = "PARAM4 / For NAV and LOITER command waypoints: Yaw orientation in degrees, [0..360] 0 = NORTH" ] param4 : f32 , # [ doc = "PARAM5 / local: x position, global: latitude" ] x : f32 , # [ doc = "PARAM6 / y position: global: longitude" ] y : f32 , # [ doc = "PARAM7 / z position: global: altitude" ] z : f32 , } # [ doc = "Request the information of the waypoint with the sequence number seq. The response of the system to this message should be a WAYPOINT message." ] pub struct WAYPOINT_REQUEST { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "Sequence" ] seq : u16 , } # [ doc = "Set the waypoint with sequence number seq as current waypoint. This means that the MAV will continue to this waypoint on the shortest path (not following the waypoints in-between)." ] pub struct WAYPOINT_SET_CURRENT { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "Sequence" ] seq : u16 , } # [ doc = "Message that announces the sequence number of the current active waypoint. The MAV will fly towards this waypoint." ] pub struct WAYPOINT_CURRENT { # [ doc = "Sequence" ] seq : u16 , } # [ doc = "Request the overall list of waypoints from the system/component." ] pub struct WAYPOINT_REQUEST_LIST { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , } # [ doc = "This message is emitted as response to WAYPOINT_REQUEST_LIST by the MAV. The GCS can then request the individual waypoints based on the knowledge of the total number of waypoints." ] pub struct WAYPOINT_COUNT { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "Number of Waypoints in the Sequence" ] count : u16 , } # [ doc = "Delete all waypoints at once." ] pub struct WAYPOINT_CLEAR_ALL { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , } # [ doc = "A certain waypoint has been reached. The system will either hold this position (or circle on the orbit) or (if the autocontinue on the WP was set) continue to the next waypoint." ] pub struct WAYPOINT_REACHED { # [ doc = "Sequence" ] seq : u16 , } # [ doc = "Ack message during waypoint handling. The type field states if this message is a positive ack (type=0) or if an error happened (type=non-zero)." ] pub struct WAYPOINT_ACK { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "0: OK, 1: Error" ] typ : u8 , } # [ doc = "As local waypoints exist, the global waypoint reference allows to transform between the local coordinate frame and the global (GPS) coordinate frame. This can be necessary when e.g. in- and outdoor settings are connected and the MAV should move from in- to outdoor." ] pub struct GPS_SET_GLOBAL_ORIGIN { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "global position * 1E7" ] latitude : i32 , # [ doc = "global position * 1E7" ] longitude : i32 , # [ doc = "global position * 1000" ] altitude : i32 , } # [ doc = "Once the MAV sets a new GPS-Local correspondence, this message announces the origin (0,0,0) position" ] pub struct GPS_LOCAL_ORIGIN_SET { # [ doc = "Latitude (WGS84), expressed as * 1E7" ] latitude : i32 , # [ doc = "Longitude (WGS84), expressed as * 1E7" ] longitude : i32 , # [ doc = "Altitude(WGS84), expressed as * 1000" ] altitude : i32 , } # [ doc = "Set the setpoint for a local position controller. This is the position in local coordinates the MAV should fly to. This message is sent by the path/waypoint planner to the onboard position controller. As some MAVs have a degree of freedom in yaw (e.g. all helicopters/quadrotors), the desired yaw angle is part of the message." ] pub struct LOCAL_POSITION_SETPOINT_SET { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "x position" ] x : f32 , # [ doc = "y position" ] y : f32 , # [ doc = "z position" ] z : f32 , # [ doc = "Desired yaw angle" ] yaw : f32 , } # [ doc = "Transmit the current local setpoint of the controller to other MAVs (collision avoidance) and to the GCS." ] pub struct LOCAL_POSITION_SETPOINT { # [ doc = "x position" ] x : f32 , # [ doc = "y position" ] y : f32 , # [ doc = "z position" ] z : f32 , # [ doc = "Desired yaw angle" ] yaw : f32 , } # [ doc = "Position fix: 0: lost, 2: 2D position fix, 3: 3D position fix " ] pub struct CONTROL_STATUS { # [ doc = "Vision position fix: 0: lost, 1: 2D local position hold, 2: 2D global position fix, 3: 3D global position fix " ] vision_fix : u8 , # [ doc = "GPS position fix: 0: no reception, 1: Minimum 1 satellite, but no position fix, 2: 2D position fix, 3: 3D position fix " ] gps_fix : u8 , # [ doc = "Attitude estimation health: 0: poor, 255: excellent" ] ahrs_health : u8 , # [ doc = "0: Attitude control disabled, 1: enabled" ] control_att : u8 , # [ doc = "0: X, Y position control disabled, 1: enabled" ] control_pos_xy : u8 , # [ doc = "0: Z position control disabled, 1: enabled" ] control_pos_z : u8 , # [ doc = "0: Yaw angle control disabled, 1: enabled" ] control_pos_yaw : u8 , } # [ doc = "Set a safety zone (volume), which is defined by two corners of a cube. This message can be used to tell the MAV which setpoints/waypoints to accept and which to reject. Safety areas are often enforced by national or competition regulations." ] pub struct SAFETY_SET_ALLOWED_AREA { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "Coordinate frame, as defined by MAV_FRAME enum in mavlink_types.h. Can be either global, GPS, right-handed with Z axis up or local, right handed, Z axis down." ] frame : u8 , # [ doc = "x position 1 / Latitude 1" ] p1x : f32 , # [ doc = "y position 1 / Longitude 1" ] p1y : f32 , # [ doc = "z position 1 / Altitude 1" ] p1z : f32 , # [ doc = "x position 2 / Latitude 2" ] p2x : f32 , # [ doc = "y position 2 / Longitude 2" ] p2y : f32 , # [ doc = "z position 2 / Altitude 2" ] p2z : f32 , } # [ doc = "Read out the safety zone the MAV currently assumes." ] pub struct SAFETY_ALLOWED_AREA { # [ doc = "Coordinate frame, as defined by MAV_FRAME enum in mavlink_types.h. Can be either global, GPS, right-handed with Z axis up or local, right handed, Z axis down." ] frame : u8 , # [ doc = "x position 1 / Latitude 1" ] p1x : f32 , # [ doc = "y position 1 / Longitude 1" ] p1y : f32 , # [ doc = "z position 1 / Altitude 1" ] p1z : f32 , # [ doc = "x position 2 / Latitude 2" ] p2x : f32 , # [ doc = "y position 2 / Longitude 2" ] p2y : f32 , # [ doc = "z position 2 / Altitude 2" ] p2z : f32 , } # [ doc = "Set roll, pitch and yaw." ] pub struct SET_ROLL_PITCH_YAW_THRUST { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "Desired roll angle in radians" ] roll : f32 , # [ doc = "Desired pitch angle in radians" ] pitch : f32 , # [ doc = "Desired yaw angle in radians" ] yaw : f32 , # [ doc = "Collective thrust, normalized to 0 .. 1" ] thrust : f32 , } # [ doc = "Set roll, pitch and yaw." ] pub struct SET_ROLL_PITCH_YAW_SPEED_THRUST { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "Desired roll angular speed in rad/s" ] roll_speed : f32 , # [ doc = "Desired pitch angular speed in rad/s" ] pitch_speed : f32 , # [ doc = "Desired yaw angular speed in rad/s" ] yaw_speed : f32 , # [ doc = "Collective thrust, normalized to 0 .. 1" ] thrust : f32 , } # [ doc = "Setpoint in roll, pitch, yaw currently active on the system." ] pub struct ROLL_PITCH_YAW_THRUST_SETPOINT { # [ doc = "Timestamp in micro seconds since unix epoch" ] time_us : u64 , # [ doc = "Desired roll angle in radians" ] roll : f32 , # [ doc = "Desired pitch angle in radians" ] pitch : f32 , # [ doc = "Desired yaw angle in radians" ] yaw : f32 , # [ doc = "Collective thrust, normalized to 0 .. 1" ] thrust : f32 , } # [ doc = "Setpoint in rollspeed, pitchspeed, yawspeed currently active on the system." ] pub struct ROLL_PITCH_YAW_SPEED_THRUST_SETPOINT { # [ doc = "Timestamp in micro seconds since unix epoch" ] time_us : u64 , # [ doc = "Desired roll angular speed in rad/s" ] roll_speed : f32 , # [ doc = "Desired pitch angular speed in rad/s" ] pitch_speed : f32 , # [ doc = "Desired yaw angular speed in rad/s" ] yaw_speed : f32 , # [ doc = "Collective thrust, normalized to 0 .. 1" ] thrust : f32 , } # [ doc = "Outputs of the APM navigation controller. The primary use of this message is to check the response and signs\nof the controller before actual flight and to assist with tuning controller parameters " ] pub struct NAV_CONTROLLER_OUTPUT { # [ doc = "Current desired roll in degrees" ] nav_roll : f32 , # [ doc = "Current desired pitch in degrees" ] nav_pitch : f32 , # [ doc = "Current desired heading in degrees" ] nav_bearing : i16 , # [ doc = "Bearing to current waypoint/target in degrees" ] target_bearing : i16 , # [ doc = "Distance to active waypoint in meters" ] wp_dist : u16 , # [ doc = "Current altitude error in meters" ] alt_error : f32 , # [ doc = "Current airspeed error in meters/second" ] aspd_error : f32 , # [ doc = "Current crosstrack error on x-y plane in meters" ] xtrack_error : f32 , } # [ doc = "The goal position of the system. This position is the input to any navigation or path planning algorithm and does NOT represent the current controller setpoint." ] pub struct POSITION_TARGET { # [ doc = "x position" ] x : f32 , # [ doc = "y position" ] y : f32 , # [ doc = "z position" ] z : f32 , # [ doc = "yaw orientation in radians, 0 = NORTH" ] yaw : f32 , } # [ doc = "Corrects the systems state by adding an error correction term to the position and velocity, and by rotating the attitude by a correction angle." ] pub struct STATE_CORRECTION { # [ doc = "x position error" ] xErr : f32 , # [ doc = "y position error" ] yErr : f32 , # [ doc = "z position error" ] zErr : f32 , # [ doc = "roll error (radians)" ] rollErr : f32 , # [ doc = "pitch error (radians)" ] pitchErr : f32 , # [ doc = "yaw error (radians)" ] yawErr : f32 , # [ doc = "x velocity" ] vxErr : f32 , # [ doc = "y velocity" ] vyErr : f32 , # [ doc = "z velocity" ] vzErr : f32 , } # [ doc = "The system setting the altitude" ] pub struct SET_ALTITUDE { # [ doc = "The new altitude in meters" ] mode : u32 , } # [ doc = "The target requested to send the message stream." ] pub struct REQUEST_DATA_STREAM { # [ doc = "The target requested to send the message stream." ] target_component : u8 , # [ doc = "The ID of the requested message type" ] req_stream_id : u8 , # [ doc = "Update rate in Hertz" ] req_message_rate : u16 , # [ doc = "1 to start sending, 0 to stop sending." ] start_stop : u8 , } # [ doc = "This packet is useful for high throughput\n                applications such as hardware in the loop simulations.\n            " ] pub struct HIL_STATE { # [ doc = "Timestamp (microseconds since UNIX epoch or microseconds since system boot)" ] usec : u64 , # [ doc = "Roll angle (rad)" ] roll : f32 , # [ doc = "Pitch angle (rad)" ] pitch : f32 , # [ doc = "Yaw angle (rad)" ] yaw : f32 , # [ doc = "Roll angular speed (rad/s)" ] rollspeed : f32 , # [ doc = "Pitch angular speed (rad/s)" ] pitchspeed : f32 , # [ doc = "Yaw angular speed (rad/s)" ] yawspeed : f32 , # [ doc = "Latitude, expressed as * 1E7" ] lat : i32 , # [ doc = "Longitude, expressed as * 1E7" ] lon : i32 , # [ doc = "Altitude in meters, expressed as * 1000 (millimeters)" ] alt : i32 , # [ doc = "Ground X Speed (Latitude), expressed as m/s * 100" ] vx : i16 , # [ doc = "Ground Y Speed (Longitude), expressed as m/s * 100" ] vy : i16 , # [ doc = "Ground Z Speed (Altitude), expressed as m/s * 100" ] vz : i16 , # [ doc = "X acceleration (mg)" ] xacc : i16 , # [ doc = "Y acceleration (mg)" ] yacc : i16 , # [ doc = "Z acceleration (mg)" ] zacc : i16 , } # [ doc = "Hardware in the loop control outputs" ] pub struct HIL_CONTROLS { # [ doc = "Timestamp (microseconds since UNIX epoch or microseconds since system boot)" ] time_us : u64 , # [ doc = "Control output -3 .. 1" ] roll_ailerons : f32 , # [ doc = "Control output -1 .. 1" ] pitch_elevator : f32 , # [ doc = "Control output -1 .. 1" ] yaw_rudder : f32 , # [ doc = "Throttle 0 .. 1" ] throttle : f32 , # [ doc = "System mode (MAV_MODE)" ] mode : u8 , # [ doc = "Navigation mode (MAV_NAV_MODE)" ] nav_mode : u8 , } # [ doc = "The system to be controlled" ] pub struct MANUAL_CONTROL { # [ doc = "roll" ] roll : f32 , # [ doc = "pitch" ] pitch : f32 , # [ doc = "yaw" ] yaw : f32 , # [ doc = "thrust" ] thrust : f32 , # [ doc = "roll control enabled auto:0, manual:1" ] roll_manual : u8 , # [ doc = "pitch auto:0, manual:1" ] pitch_manual : u8 , # [ doc = "yaw auto:0, manual:1" ] yaw_manual : u8 , # [ doc = "thrust auto:0, manual:1" ] thrust_manual : u8 , } # [ doc = "The RAW values of the RC channels sent to the MAV to override info received from the RC radio. A value of -1 means no change to that channel. A value of 0 means control of that channel should be released back to the RC radio. The standard PPM modulation is as follows: 1000 microseconds: 0%, 2000 microseconds: 100%. Individual receivers/transmitters might violate this specification." ] pub struct RC_CHANNELS_OVERRIDE { # [ doc = "System ID" ] target_system : u8 , # [ doc = "Component ID" ] target_component : u8 , # [ doc = "RC channel 1 value, in microseconds" ] chan1_raw : u16 , # [ doc = "RC channel 2 value, in microseconds" ] chan2_raw : u16 , # [ doc = "RC channel 3 value, in microseconds" ] chan3_raw : u16 , # [ doc = "RC channel 4 value, in microseconds" ] chan4_raw : u16 , # [ doc = "RC channel 5 value, in microseconds" ] chan5_raw : u16 , # [ doc = "RC channel 6 value, in microseconds" ] chan6_raw : u16 , # [ doc = "RC channel 7 value, in microseconds" ] chan7_raw : u16 , # [ doc = "RC channel 8 value, in microseconds" ] chan8_raw : u16 , } # [ doc = "The filtered global position (e.g. fused GPS and accelerometers). The position is in GPS-frame (right-handed, Z-up)" ] pub struct GLOBAL_POSITION_INT { # [ doc = "Latitude, expressed as * 1E7" ] lat : i32 , # [ doc = "Longitude, expressed as * 1E7" ] lon : i32 , # [ doc = "Altitude in meters, expressed as * 1000 (millimeters)" ] alt : i32 , # [ doc = "Ground X Speed (Latitude), expressed as m/s * 100" ] vx : i16 , # [ doc = "Ground Y Speed (Longitude), expressed as m/s * 100" ] vy : i16 , # [ doc = "Ground Z Speed (Altitude), expressed as m/s * 100" ] vz : i16 , } # [ doc = "Metrics typically displayed on a HUD for fixed wing aircraft" ] pub struct VFR_HUD { # [ doc = "Current airspeed in m/s" ] airspeed : f32 , # [ doc = "Current ground speed in m/s" ] groundspeed : f32 , # [ doc = "Current heading in degrees, in compass units (0..360, 0=north)" ] heading : i16 , # [ doc = "Current throttle setting in integer percent, 0 to 100" ] throttle : u16 , # [ doc = "Current altitude (MSL), in meters" ] alt : f32 , # [ doc = "Current climb rate in meters/second" ] climb : f32 , } # [ doc = "Send a command with up to four parameters to the MAV" ] pub struct COMMAND { # [ doc = "System which should execute the command" ] target_system : u8 , # [ doc = "Component which should execute the command, 0 for all components" ] target_component : u8 , # [ doc = "Command ID, as defined by MAV_CMD enum." ] command : u8 , # [ doc = "0: First transmission of this command. 1-255: Confirmation transmissions (e.g. for kill command)" ] confirmation : u8 , # [ doc = "Parameter 1, as defined by MAV_CMD enum." ] param1 : f32 , # [ doc = "Parameter 2, as defined by MAV_CMD enum." ] param2 : f32 , # [ doc = "Parameter 3, as defined by MAV_CMD enum." ] param3 : f32 , # [ doc = "Parameter 4, as defined by MAV_CMD enum." ] param4 : f32 , } # [ doc = "Report status of a command. Includes feedback whether the command was executed" ] pub struct COMMAND_ACK { # [ doc = "Current airspeed in m/s" ] command : f32 , # [ doc = "1: Action ACCEPTED and EXECUTED, 1: Action TEMPORARY REJECTED/DENIED, 2: Action PERMANENTLY DENIED, 3: Action UNKNOWN/UNSUPPORTED, 4: Requesting CONFIRMATION" ] result : f32 , } # [ doc = "Optical flow from a flow sensor (e.g. optical mouse sensor)" ] pub struct OPTICAL_FLOW { # [ doc = "Timestamp (UNIX)" ] time : u64 , # [ doc = "Sensor ID" ] sensor_id : u8 , # [ doc = "Flow in pixels in x-sensor direction" ] flow_x : i16 , # [ doc = "Flow in pixels in y-sensor direction" ] flow_y : i16 , # [ doc = "Optical flow quality / confidence. 0: bad, 255: maximum quality" ] quality : u8 , # [ doc = "Ground distance in meters" ] ground_distance : f32 , } # [ doc = "Object has been detected" ] pub struct OBJECT_DETECTION_EVENT { # [ doc = "Timestamp in milliseconds since system boot" ] time : u32 , # [ doc = "Object ID" ] object_id : u16 , # [ doc = "Object type: 0: image, 1: letter, 2: ground vehicle, 3: air vehicle, 4: surface vehicle, 5: sub-surface vehicle, 6: human, 7: animal" ] typ : u8 , # [ doc = "Name of the object as defined by the detector" ] name : u8 , # [ doc = "Detection quality / confidence. 0: bad, 255: maximum confidence" ] quality : u8 , # [ doc = "Angle of the object with respect to the body frame in NED coordinates in radians. 0: front" ] bearing : f32 , # [ doc = "Ground distance in meters" ] distance : f32 , } # [ doc = "Name" ] pub struct DEBUG_VECT { # [ doc = "Timestamp" ] usec : u64 , # [ doc = "x" ] x : f32 , # [ doc = "y" ] y : f32 , # [ doc = "z" ] z : f32 , } # [ doc = "Send a key-value pair as float. The use of this message is discouraged for normal packets, but a quite efficient way for testing new messages and getting experimental debug output." ] pub struct NAMED_VALUE_FLOAT { # [ doc = "Name of the debug variable" ] name : u8 , # [ doc = "Floating point value" ] value : f32 , } # [ doc = "Send a key-value pair as integer. The use of this message is discouraged for normal packets, but a quite efficient way for testing new messages and getting experimental debug output." ] pub struct NAMED_VALUE_INT { # [ doc = "Name of the debug variable" ] name : u8 , # [ doc = "Signed integer value" ] value : i32 , } # [ doc = "Status text message. These messages are printed in yellow in the COMM console of QGroundControl. WARNING: They consume quite some bandwidth, so use only for important status and error messages. If implemented wisely, these messages are buffered on the MCU and sent only at a limited rate (e.g. 10 Hz)." ] pub struct STATUSTEXT { # [ doc = "Severity of status, 0 = info message, 255 = critical fault" ] severity : u8 , # [ doc = "Status text message, without null termination character" ] text : u8 , } # [ doc = "Send a debug value. The index is used to discriminate between values. These values show up in the plot of QGroundControl as DEBUG N." ] pub struct DEBUG { # [ doc = "index of debug variable" ] ind : u8 , # [ doc = "DEBUG value" ] value : f32 , }